// globalContext.js

import React, { useContext, useState } from "react";
import axios from 'axios';

const BASE_URL = "http://localhost:5000/api/v1/";

const GlobalContext = React.createContext();

export const GlobalProvider = ({ children }) => {
  const [incomes, setIncomes] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [incomeData, setIncomeData] = useState(null);
  const [expenseData, setExpenseData] = useState(null);
  const [error, setError] = useState(null);

  const addIncome = async (income) => {
    try {
      await axios.post(`${BASE_URL}add-income`, income);
      getIncomes();
    } catch (err) {
      setError(err.response.data.message);
    }
  };

  const getIncomes = async () => {
    const response = await axios.get(`${BASE_URL}get-incomes`);
    setIncomes(response.data);
  };

  const deleteIncome = async (id) => {
    await axios.delete(`${BASE_URL}delete-income/${id}`);
    getIncomes();
  };

  const totalIncome = () => {
    return incomes.reduce((acc, income) => acc + income.amount, 0);
  };

  const addExpense = async (expense) => {
    try {
      await axios.post(`${BASE_URL}add-expense`, expense);
      getExpenses();
    } catch (err) {
      setError(err.response.data.message);
    }
  };

  const getExpenses = async () => {
    const response = await axios.get(`${BASE_URL}get-expenses`);
    setExpenses(response.data);
  };

  const deleteExpense = async (id) => {
    await axios.delete(`${BASE_URL}delete-expense/${id}`);
    getExpenses();
  };

  const totalExpenses = () => {
    return expenses.reduce((acc, expense) => acc + expense.amount, 0);
  };

  const totalBalance = () => {
    return totalIncome() - totalExpenses();
  };

  const transactionHistory = () => {
    const history = [...incomes, ...expenses];
    history.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
    return history.slice(0, 3);
  };

  const getDropdownOptions = async () => {
    try {
      const response = await axios.get(`${BASE_URL}dropdown-options`);
      // Set options in your state or use them directly as needed
    } catch (error) {
      console.error(error);
      setError('Error fetching dropdown options');
    }
  };

  const generateReport = async (filterOptions) => {
    try {
      const response = await axios.get(`${BASE_URL}generate-report`, { params: filterOptions });

      // Log the complete response for debugging
      console.log('Generate Report Response:', response);

      // Check the structure of the response.data
      console.log('Response Data:', response.data);

      // Check if incomeData and expenseData are present in the response
      if (response.data.hasOwnProperty('incomeData') && response.data.hasOwnProperty('expenseData')) {
        console.log('Income Data:', response.data.incomeData);
        console.log('Expense Data:', response.data.expenseData);

        setIncomeData(response.data.incomeData);
        setExpenseData(response.data.expenseData);
      } else {
        console.error('IncomeData or ExpenseData not present in the response:', response.data);
        setError('Error generating report');
      }
    } catch (error) {
      console.error(error);
      setError('Error generating report');
    }
  };

  return (
    <GlobalContext.Provider
      value={{
        addIncome,
        getIncomes,
        incomes,
        deleteIncome,
        expenses,
        totalIncome,
        addExpense,
        getExpenses,
        deleteExpense,
        totalExpenses,
        totalBalance,
        transactionHistory,
        getDropdownOptions,
        generateReport,
        incomeData,
        expenseData,
        error,
        setError
      }}
    >
      {children}
    </GlobalContext.Provider>
  );
};

export const useGlobalContext = () => {
  return useContext(GlobalContext);
};
